def bits_to_string(bit_string):
    # Ensure the length of the bit string is a multiple of 8
    if len(bit_string) % 8 != 0:
        raise ValueError("The bit string's length must be a multiple of 8.")

    # Split the bit string into chunks of 8 bits
    characters = [
        chr(int(bit_string[i:i+8], 2)) for i in range(0, len(bit_string), 8)
    ]
    
    # Combine characters into a string
    return ''.join(characters)

# Example usage
bit_string = ""
result = bits_to_string(bit_string)
print(f"The string of characters is: {result}")